import React, { useContext,useState, useEffect, useRef } from 'react'
import notecontext from '../context/notes/notecontext'
import AddNote from './AddNote'
import NoteItem from './NoteItem'

const Notess = () => {
    const context = useContext(notecontext)
    const { notes, getNotes } = context
    const [note, setnote] = useState({etitle:"",edescription:"",etag:""})
    useEffect(() => {
        getNotes();
    }, [])

    const updateNote = (currentNote) => {
        ref.current.click()
        setnote(currentNote)
    }

    const handleAddclick=(e)=>{
        console.log("updating note",note);

        //so that page doesnt reload
        e.preventDefault();
    }

    const onChange=(e)=>{
        //spread operator
        setnote({...note,[e.target.name]:e.target.value})
    }

    const ref = useRef(null)
    return (
        <>
            <AddNote />
            <button type="button" ref={ref} className="d-none btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal">
                Launch demo modal
            </button>

            <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel">Edit Note</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <form>
                                <div className="mb-3">
                                    <label htmlfor="etitle" className="form-label">Title</label>
                                    <input type="text" value={note.etitle}className="form-control" id="etitle" name="etitle" aria-describedby="emailHelp" onChange={onChange} />
                                </div>
                                <div className="mb-3">
                                    <label htmlfor="edescription" className="form-label">Description</label>
                                    <input type="text" value={note.edescription}className="form-control" id="edescription" name="edescription" onChange={onChange} />
                                </div>
                                <div className="mb-3">
                                    <label htmlfor="etag" className="form-label">Tag</label>
                                    <input type="text" value={note.etag}className="form-control" id="etag" name="etag" onChange={onChange} />
                                </div>

                            </form>
                        </div>
                        <div className="modal-footer">
                            <button type="button"  className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                            <button type="button" onClick={handleAddclick} className="btn btn-primary">Update Note</button>
                        </div>
                    </div>
                </div>
            </div>
            <div className="row my-3">
                <h2 >Your Notes</h2>
                {
                    notes.map((note) => {
                        return <NoteItem key={note._id} updateNote={updateNote} note={note} />;
                    })
                }
            </div>
        </>
    )
}

export default Notess
