import notecontext from "./notecontext";
import { useState } from "react";
const NoteState = (props) => {

  const host="http://localhost:4000"

    const notesinitial =[]

    const [notes, setnotes] = useState(notesinitial)


    //get all  notes
    const getNotes = async ()=>{

      const response = await fetch(`${host}/api/notes/fetchallnotes`, {

        method: 'GET', // *GET, POST, PUT, DELETE, etc
        headers: {
          'Content-Type': 'application/json',
          "auth-token":"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjNkN2IzYmZiYjVkYTEyN2E0MTdlMjJlIn0sImlhdCI6MTY3NTA4MzA2Mn0.RUbuvpO0QzAaiboeGo0KUb_1VCMcjIn4oRZZcjb94uQ"
        },
      });
      const json=await response.json();
      console.log(json);
    }

    //add notes
    const addNote = async (title,description,tag)=>{

      const response = await fetch(`${host}/api/notes/addnote`, {

        method: 'POST', // *GET, POST, PUT, DELETE, etc
        headers: {
          'Content-Type': 'application/json',
          "auth-token":"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjNkN2IzYmZiYjVkYTEyN2E0MTdlMjJlIn0sImlhdCI6MTY3NTA4MzA2Mn0.RUbuvpO0QzAaiboeGo0KUb_1VCMcjIn4oRZZcjb94uQ"
        },
        body: JSON.stringify({title,description,tag}) // body data type must match "Content-Type" header
      });
      console.log("adding note");
      const note={
        "_id": "63d91d44a010ebbd3339cb907",
        "user": "63d7b3bfbb5da127a417e22e",
        "title": title,
        "description": description,
        "tag": tag,
        "date": "2023-01-31T13:53:08.067Z",
        "__v": 0
      };
      setnotes(notes.concat(note))
    }

    //delete note
    const deleteNote =(id)=>{
      console.log("note deleted of id "+ id);

      //added to new notes only if notes id != given id
      const newNotes=notes.filter((n)=>{return n._id!==id})
      setnotes(newNotes)

    }

    //edit note 
    const editNote = async(id,title,description,tag)=>{


      const response = await fetch(`${host}/api/notes/updatenote/${id}`, {

        method: 'POST', // *GET, POST, PUT, DELETE, etc
        headers: {
          'Content-Type': 'application/json',
          "auth-token":"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjNkN2IzYmZiYjVkYTEyN2E0MTdlMjJlIn0sImlhdCI6MTY3NTA4MzA2Mn0.RUbuvpO0QzAaiboeGo0KUb_1VCMcjIn4oRZZcjb94uQ"
        },
        body: JSON.stringify({title,description,tag}) // body data type must match "Content-Type" header
      });
      const json= response.json(); // parses JSON response into native JavaScript objects

      //for loop search for note with that id
      for (let index = 0; index < notes.length; index++) {
        const element = notes[index];
        if(element._id===id){
          element.title=title;
          element.description=description;
          element.tag=tag
        }
      }
    }

    return (
        <notecontext.Provider value={{notes,addNote,deleteNote,editNote,getNotes}}>
            {props.children}
        </notecontext.Provider>
    )
}

export default NoteState;
